import { useState } from "react";
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";



function App() {

  const [todos,setTodos] = useState([{name:'Do coding challenges', done:false},{name:'Do coding challenges', done:false},{name:'Do coding challenges', done:true}])

  console.table(todos)

  const addTodo = (e)=>{
    const todo = {name:e, done:false}
    const newTodos = [...todos]
    newTodos.push(todo)
    console.log(newTodos)
    setTodos(newTodos)
  }

  const toggleTodo = (index)=>{
    const newTodos = [...todos]
    newTodos[index].done = !newTodos[index].done
    setTodos(todos)
    console.log(newTodos)
  }

  const removeTodo = (index)=>{
    const newTodos = [...todos]
    newTodos.splice(index,1)
    setTodos(newTodos)
    console.table(todos)
  }




  return (
   <>
    <button onClick={removeTodo}>prova</button>
   </>
  );
}

export default App;
