import { useState } from "react";
import Todo from "./Todo";

const Active = ({todos,addTodo,toggleTodo}) => {

    // const filteredArray = todos.filter((todo)=>{
    //     if(!todo.done) {
    //         return true
    //     }
    // })

    // const [filteredTodos,setFilteredTodos] = useState([filteredArray])


    return ( 
        <>
            <form action="#" onSubmit={(e)=>{
                e.preventDefault()
                const form = document.querySelector('form')
                addTodo(form.name.value)
                form.reset()
            }}>
                <input type="text" name="name"/>
                <button>Add</button>
            </form>

            {todos.map((todo,index)=>{
                if(!todo.done) {
                    return <Todo todo={todo} index={index} toggleTodo={toggleTodo}></Todo>
                }

            })}
        </>
     );
}

export default Active;
