const Todo = ({todo,index,toggleTodo}) => {
    return ( 
        <div className="todo-container" key={index}>
            {(todo.done)? <input type="checkbox" name="" id="" index={index} checked onChange={()=>{toggleTodo(index)}}/> : <input type="checkbox" name="" index={index} id="" onChange={()=>{toggleTodo(index)}}/>}
            <p>{todo.name}</p> 
        </div>
     );
}

export default Todo;
