import { useState } from "react";
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";
import Home from "./Home";
import Active from "./Active";



function App() {

  const [todos,setTodos] = useState([{name:'Do coding challenges', done:false},{name:'Do coding challenges', done:false},{name:'Do coding challenges', done:true}])

  console.table(todos)

  const addTodo = (name)=>{
    const todo = {name:name, done:false}
    const newTodos = [...todos]
    newTodos.push(todo)
    console.log(newTodos)
    setTodos(newTodos)
  }

  const toggleTodo = (index)=>{
    const newTodos = [...todos]
    newTodos[index].done = !newTodos[index].done
    setTodos(todos)
    console.log(newTodos)
  }

  const removeTodo = (index)=>{
    const newTodos = [...todos]
    newTodos.splice(index,1)
    setTodos(newTodos)
    console.table(todos)
  }




  return (
    <BrowserRouter>
      <Routes>
        <Route path='/' element={<Home todos={todos} addTodo={addTodo} toggleTodo={toggleTodo}/>}></Route>
        <Route path='/' element={<Active></Active>}></Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
